(function(){"use strict";document.querySelector('[data-toggle="offcanvas"]').addEventListener("click",function(){document.querySelector(".offcanvas-collapse").classList.toggle("open");});})();$(function(){var nav=$('.navbar-collapse'),animateTime=500,navLink=$('.navbar-toggler ');navLink.click(function(){if(nav.height()===0){autoHeightAnimate(nav,animateTime);}else{nav.stop().animate({height:'0'},animateTime);}});})
function autoHeightAnimate(element,time){var curHeight=element.height(),autoHeight=element.css('height','auto').height();element.height(curHeight);element.stop().animate({height:autoHeight},time);}
jQuery('.navbar-toggler').on('click',function(){jQuery(this).toggleClass('change')});jQuery(document).ready(function(){jQuery(window).scroll(function(){if(jQuery(window).scrollTop()>=1){jQuery('.top-header').addClass('fixed');}
else{jQuery('.top-header').removeClass('fixed');}});});$(document).ready(function(){function closeDropdownOnScroll(){$('.dropdown-menu').collapse('hide');}
$(document).on('scroll',function(){closeDropdownOnScroll();});});